import React, { Component } from "react";

export default class component extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cheese: 1
    };
  }
  output = () => {
    if (this.state.cheese === 1) {
      return (
        <div>
          {this.props.workArray.map(workarray => (
            <li>{workarray.name}</li>
          ))}
        </div>
      );
    } else if (this.state.cheese === 2) {
      return <div>button 2 pressed</div>;
    }
  };

  handleButton1 = () => {
    this.setState({ cheese: 1 });
  };
  handleButton2 = () => {
    this.setState({ cheese: 2 });
  };

  render() {
    return (
      <div>
        <button onClick={this.handleButton1}>Button 1</button>
        <button onClick={this.handleButton2}>Button 2</button>
        <p> the age is: {this.props.age}</p> {this.output()}
        <ul></ul>
      </div>
    );
  }
}
